import React from 'react'
import CauHoi from './DapAn'
export default class Quiz extends React.Component{
    constructor(props){
        super(props);
        this.state={
            list:[],
            point:0,
            value:""
        }
        this.checkQuestion = this.checkQuestion.bind(this);
    }
    componentDidMount(){
        fetch("https://opentdb.com/api.php?amount=5&category=21&difficulty=easy&type=multiple")
        .then(res=>res.json())
        .then(data=> this.setState({list:data.results}))
        // this.state({list:data.results}))
    }
    checkQuestion(){
       let points = this.state.point;
        this.setState({
            point:points+10,
            value:this.state.value
        })

    }
    render(){
        console.log(this.state.list)
        console.log(this.state.value)
        return (
            <CauHoi list={this.state.list} checkQuestion={this.checkQuestion} point={this.state.point}/>
        )
    }
}
